mapper.select:
select count(*)
from Member
where m_nickname=#{m_nickname}

select m_uid, m_email, m_pw, m_nickname, m_addr, m_regdate, m_enabled, m_deldate
from Member
where m_email = #{m_email, jdbcType=VARCHAR} and m_pw = #{m_pw, jdbcType=VARCHAR}

select m_email, m_pw
from Member
where m_email = #{m_email}

mapper.update:
update Member
set m_pw= #{m_pw}, m_nickname = #{m_nickname}, m_addr=#{m_addr}
where m_uid=#{m_uid}

update Member
set m_pw= #{m_pw}, m_nickname = #{m_nickname}, m_addr=#{m_addr}
where m_uid=#{m_uid}

update Creator
set create_name=#{create_name}, create_intro=#{create_intro} 
where m_uid=#{m_uid}

mapper.select:
select m_uid, m_email, m_pw, m_nickname, m_addr, m_type
from Member
where m_uid=#{m_uid}
