mapper.select:
SELECT P_ID, TITLE_ORIGIN_NAME, TITLE_STORED_NAME, TITLE_STORED_PATH, TITLE_FILE_SIZE
FROM Project_title_img
WHERE P_ID = #{p_id}

mapper.update:
UPDATE Project_title_img
SET TITLE_ORIGIN_NAME = #{title_origin_name}, TITLE_STORED_NAME = #{title_stored_name}, TITLE_STORED_PATH = #{title_stored_path}, TITLE_FILE_SIZE = #{title_file_size}
WHERE P_ID = #{p_id}
